function [Delta_zeta_k,Delta_eta_p,Delta_eta_dot_p] = Variation_Prediction_Platform(Config,Var)Theta = Var.mu_p(1:3);% R_p_n = Var.R_p_n;% R_0 = Var.g_0(1:3,1:3);R_p_n = Var.R_p_n;R_0 = R_p_n*Exp_SO3_(Var.mu_p(1:3));a = Config.Integrateur.newmark.a;b = Config.Integrateur.newmark.b;eta_0 = Var.eta_0;eta_dot_0 = Var.eta_0_dot;Delta_zeta_k =  [[T_SO3_(Theta),zeros(3)];[zeros(3),R_0']]*Var.Delta_mu_p;Delta_eta_p = Config.simu.dyn*[[a*eye(3),zeros(3)];[hat_(eta_0(4:6))*T_SO3_(Theta),a*R_0']]*Var.Delta_mu_p;Delta_eta_dot_p =  Config.simu.dyn*[[b*eye(3),zeros(3)];[hat_(eta_dot_0(4:6)+hat_(eta_0(1:3))*eta_0(4:6))*T_SO3_(Theta)+a*hat_(eta_0(4:6))-hat_(eta_0(1:3))*hat_(eta_0(4:6))*T_SO3_(Theta),b*R_0'-a*hat_(eta_0(1:3))*R_0']]*Var.Delta_mu_p;  